class Solution {
 public:
  int maxProfit(vector<int>& prices) {
    if (prices.empty()) {
      return 0;
    }
    vector<int> minp(prices.size(), 0);
    vector<int> dp(prices.size(), 0);
    dp[0] = 0;
    minp[0] = prices[0];
    for (int i = 1; i < minp.size(); i++) {
      minp[i] = min(minp[i - 1], prices[i]);
    }
    for (int i = 1; i < dp.size(); i++) {
      dp[i] = max(dp[i - 1], prices[i] - minp[i]);
    }
    return dp[dp.size() - 1];
  }
};