// 202 Happy Number
// https://leetcode.com//problems/happy-number/description/
// Fetched at 2018-07-24
// Submitted 2 years, 1 month ago
// Runtime: 4 ms
// This solution defeats 10.45% cpp solutions

class Solution {
 public:
  bool isHappy(int n) {
    if (n == 1 || n == 7) {
      return true;
    }
    if (n < 10) {
      return false;
    }
    int sum = 0;
    int bits = (int)(log(n) / log(10));
    if (pow(10, bits) > n) {
      bits--;
    }
    for (int i = 0; i <= bits; i++) {
      sum += pow((int)(n / pow(10, i)) % 10, 2);
    }
    return isHappy(sum);
  }
};