// Runtime: 396 ms

class Solution {
 public:
  int findKthLargest(vector<int>& nums, int k) {
    int times = 0;
    while (times < k - 1) {
      for (int i = nums.size() / 2; i >= 0; i--) {
        if (2 * i < nums.size() && nums[2 * i] > nums[i]) {
          swap(nums[2 * i], nums[i]);
        }
        if (2 * i + 1 < nums.size() && nums[2 * i + 1] > nums[i]) {
          swap(nums[2 * i + 1], nums[i]);
        }
      }
      nums.erase(nums.begin());
      times++;
    }
    for (int i = nums.size() / 2; i >= 0; i--) {
      if (2 * i < nums.size() && nums[2 * i] > nums[i]) {
        swap(nums[2 * i], nums[i]);
      }
      if (2 * i + 1 < nums.size() && nums[2 * i + 1] > nums[i]) {
        swap(nums[2 * i + 1], nums[i]);
      }
    }
    return nums[0];
  }
};