class Solution {
 public:
  vector<int> productExceptSelf(vector<int>& nums) {
    if (nums.empty()) {
      return nums;
    }
    vector<int> output(nums.size(), 1);
    int begin = 1, end = 1;
    for (int i = 0; i < nums.size(); i++) {
      output[i] *= begin;
      begin *= nums[i];
      output[nums.size() - i - 1] *= end;
      end *= nums[nums.size() - 1 - i];
    }
    return output;
  }
};