// 243 Shortest Word Distance
// https://leetcode.com//problems/shortest-word-distance/description/
// Fetched at 2018-07-24
// Submitted 5 months ago
// Runtime: 15 ms
// This solution defeats 3.76% cpp solutions

class Solution {
 public:
  int shortestDistance(vector<string>& words, string word1, string word2) {
    int ret = words.size();
    for (int i = 0; i < words.size(); i++) {
      if (words[i] == word1) {
        int j = 1;
        while (true) {
          if (j + i < words.size() && words[j + i] == word2) {
            ret = min(ret, j);
            break;
          }
          if (i - j >= 0 && words[i - j] == word2) {
            ret = min(ret, j);
            break;
          }
          j++;
        }
      }
    }
    return ret;
  }
};