// Runtime: 60 ms
// This solution defeats 4.71% cpp solutions

class Solution {
 public:
  int removeDuplicates(vector<int>& nums) {
    if (nums.size() < 2) {
      return nums.size();
    }
    for (auto it1 = nums.begin(); it1 != nums.end();) {
      auto it2 = it1;
      while (it2 != nums.end() && *it2 == *it1) {
        it2++;
      }
      if ((int)(it2 - it1) > 1) {
        it1 = nums.erase(it1 + 1, it2);
      } else {
        it1++;
      }
    }
    return nums.size();
  }
};