class Solution {
 public:
  bool canPermutePalindrome(string s) {
    map<char, int> hash;
    for (auto a : s) {
      hash[a]++;
    }
    bool flag = true;
    for (auto it = hash.begin(); it != hash.end(); it++) {
      if (it->second % 2 == 1) {
        if (flag == true) {
          flag = false;
        } else {
          return false;
        }
      }
    }
    return true;
  }
};