class Solution {
 public:
  string flip(string s, int i) {
    s[i] = '-';
    s[i + 1] = '-';
    return s;
  }
  vector<string> generatePossibleNextMoves(string s) {
    vector<string> ret;
    if (s.empty()) {
      return ret;
    }
    for (int i = 0; i < s.size() - 1; i++) {
      if (s[i] == s[i + 1] && s[i] == '+') {
        ret.push_back(flip(s, i));
      }
    }
    return ret;
  }
};