// Forward declaration of guess API.
// @param num, your guess
// @return -1 if my number is lower, 1 if my number is higher, otherwise return
// 0
int guess(int num);

class Solution {
 public:
  int guessNumber(int n) {
    int low = 1;
    long long int high = n;
    long long int mid;
    int judge;
    while (low <= high) {
      mid = (low + high) / 2;
      judge = guess(mid);
      if (judge == 0) {
        return mid;
      } else if (judge > 0) {
        low = mid + 1;
      } else {
        high = mid - 1;
      }
    }
    return mid;
  }
};