// 405 Convert a Number to Hexadecimal
// https://leetcode.com//problems/convert-a-number-to-hexadecimal/description/
// Fetched at 2018-07-24
// Submitted 1 year, 9 months ago
// Runtime: 3 ms

class Solution {
 public:
  string tohex(long long int num) {
    if (num < 0) {
      num += pow(2, 32);
    }
    if (num == 0) {
      return "0";
    }
    if (num < 16) {
      string temp = "";
      temp.push_back(str[num]);
      return temp;
    } else {
      string temp = tohex(num / 16);
      temp.push_back(str[num % 16]);
      int i = 0;
      while (temp[0] == '0') {
        i++;
      }
      temp.erase(temp.begin(), temp.begin() + i);
      return temp;
    }
  }
  string toHex(int num) { return tohex(num); }
  string str = "0123456789abcdef";
};