class Solution {
 public:
  int jump(vector<int>& nums) {
    if (nums.size() < 2) {
      return 0;
    }
    int i = 0;
    int times = 0;
    while (i < nums.size() - 1) {
      times++;
      int max = 0;
      int steps = nums[i];
      int start = i;
      if (steps + start >= nums.size() - 1) {
        return times;
      }
      for (int j = i; j <= steps + start; j++) {
        if (j < nums.size() && nums[j] + j > max) {
          i = j;
          max = nums[j] + j;
        }
      }
    }
    return times;
  }
};