class Solution {
 public:
  int findMinArrowShots(vector<pair<int, int>>& points) {
    sort(points.begin(), points.end(), [](pair<int, int> a, pair<int, int> b) {
      return a.second < b.second;
    });
    int i = 0;
    int ret = 0;
    while (i < points.size()) {
      int arrow = points[i].second;
      ret++;
      while (i < points.size() && arrow >= points[i].first) {
        i++;
      }
    }
    return ret;
  }
};