class Solution {
 public:
  string convertToBase7(int num) {
    int n = abs(num);
    string ret = "";
    do {
      ret = to_string(n % 7) + ret;
      n /= 7;
    } while (n);
    if (num < 0) {
      ret = "-" + ret;
    }
    return ret;
  }
};