class Solution {
 public:
  int change(int amount, vector<int>& coins) {
    vector<int> dp(amount + 1, 0);
    vector<int> max_val(amount + 1, 0);
    dp[0] = 1;

    for (auto c : coins) {
      for (int i = 1; i <= amount; i++) {
        if (i - c >= 0) {
          dp[i] += dp[i - c];
        }
      }
    }
    return dp[amount];
  }
};