class Solution {
 public:
  int lengthOfLastWord(string s) {
    if (s.empty()) {
      return 0;
    }
    int end = s.size() - 1;
    while (s[end] == ' ') {
      end--;
    }
    int start = end;
    while (start >= 0 && s[start] != ' ') {
      start--;
    }
    return end - start;
  }
};