// Runtime: 92 ms
// This solution defeats 1.23% cpp solutions

class Solution {
 public:
#define tags -pow(10, 7)
  void setZeroes(vector<vector<int>>& matrix) {
    for (int i = 0; i < matrix.size(); i++) {
      for (int j = 0; j < matrix[0].size(); j++) {
        if (matrix[i][j] == 0) {
          for (int k = 0; k < matrix[i].size(); k++) {
            if (matrix[i][k] != 0) {
              matrix[i][k] = tags;
            }
          }
          for (int k = 0; k < matrix.size(); k++) {
            if (matrix[k][j] != 0) {
              matrix[k][j] = tags;
            }
          }
        }
      }
    }
    for (int i = 0; i < matrix.size(); i++)
      for (int j = 0; j < matrix[0].size(); j++) {
        if (matrix[i][j] == tags) {
          matrix[i][j] = 0;
        }
      }
  }
};