/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
 public:
  ListNode* deleteDuplicates(ListNode* head) {
    if (head == nullptr || head->next == nullptr) {
      return head;
    }
    ListNode* p = head;
    ListNode* pre = p;
    p = p->next;
    bool tags = false;
    ListNode* q = head;
    while (pre->next != nullptr && pre->val == pre->next->val) {
      tags = true;
      pre = pre->next;
    }
    if (pre->next == nullptr) {
      return nullptr;
    }
    if (tags == true) {
      head = pre->next;
      pre->next = nullptr;
      pre = head;
      p = pre->next;
      return deleteDuplicates(head);
    }
    tags = false;
    while (p != nullptr && p->next != nullptr) {
      q = p->next;
      tags = false;
      while (q != nullptr && q->val == p->val) {
        tags = true;
        q = q->next;
      }
      if (tags == true) {
        pre->next = q;
        p = q;
      } else {
        pre = pre->next;
        p = p->next;
      }
    }
    return head;
  }
};